<?php// Конттроллер страницы чтения.class C_Log extends C_Base{	// Конструктор.	function __construct()	{			}		// Виртуальный обработчик запроса.	protected function OnInput()	{		parent::OnInput();			function debug_log ($msg){	$time = date ('Y-M-D H:i:s');	$f = fopen ('data/log.txt', 'a+');	fputs ($f, "$time - $msg \n");	fclose ($f);}		debug_log ('Start'); //старт программыif (4 < 5)  // тут сложное условие наприме: isset POST от чего-то. 			// в зависимости от проверки мы попадаем в то или иной блок кода{	debug_log ('попали сюда');	echo ('попали сюда');	// какой-то код}else{	debug_log('попали туда');	echo ('попали туда');	// какой-то код}debug_log ('end'); //завершение программы	}		//	// Виртуальный генератор HTML.	//		protected function OnOutput()	{		$page = $this->Template('theme/v_log.php');						echo $page;		}	}