<?php// Базовый класс контроллера.abstract class Controller{	// Конструктор.	function __construct()	{			}		// Полная обработка HTTP запроса.	public function Request()	{		$this->OnInput();		$this->OnOutput();	}		// Виртуальный обработчик запроса.	protected function OnInput()	{	}		// Виртуальный генератор HTML.	protected function OnOutput()	{	}		// Генерация HTML шаблона в строку.	protected function Template($fileName)	{		// Генерация HTML в строку.		ob_start();		include $fileName;	}	}